import pandas as pd
df = pd.read_csv('source_files/Statlog.csv')
numerical_features = ['Duration in month', 'Credit amount', 'Installment rate in percentage of disposable income', 'Age in years']
for feature in numerical_features:
    bins = 10
    df[feature + '_binned'] = pd.cut(df[feature], bins=bins)
    bin_counts = df[feature + '_binned'].value_counts()
    max_count = bin_counts.max()
    min_count = bin_counts.min()
    if min_count == 0:
        print(f'Extreme bias detected in feature {feature}.')
    else:
        ratio = max_count / min_count
        if ratio > 100:
            print(f'Extreme bias detected in feature {feature}.')
        elif ratio > 10:
            print(f'Significant bias detected in feature {feature}.')
        elif ratio > 5:
            print(f'Moderate bias detected in feature {feature}.')
        elif ratio > 2:
            print(f'Minimal bias detected in feature {feature}.')
        else:
            print(f'No bias detected in feature {feature}.')
